import org.junit.Test;

import static org.junit.Assert.assertEquals;

import org.junit.Before;    
    
public class ProgrammersDoubleDeleteTest {
    ProgrammersDoubleDelete p = new ProgrammersDoubleDelete();
        
    @Test
    public void testA() {
        String input = "baabaa";
        int expected = 1;
        int actual = p.solution(input);

        assertEquals(expected, actual);
    }

    @Test
    public void testB() {
        String input = "cdcd";
        int expected = 0;
        int actual = p.solution(input);
        
        assertEquals(expected, actual);
    }

    @Test
    public void testC() {
        String input = "";
        int expected = 1;
        int actual = p.solution(input);
        
        assertEquals(expected, actual);
    }
}
    